mapper.update:
UPDATE REPLY
SET REPLY_CONTENT = #{replyContent}
WHERE REPLY_NO =#{replyNo}

UPDATE REPLY
SET STATUS = 'N'
WHERE REPLY_NO = #{replyNo}

UPDATE BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_TYPE
FROM BULLETIN_BOARD
WHERE BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM LIKED
WHERE REF_BNO = #{refBno}
AND REF_UNO = #{refUno}

mapper.select:
SELECT LIKED_TYPE
FROM LIKED
WHERE REF_BNO = #{refBno}
AND REF_UNO = #{refUno}

mapper.update:
UPDATE BOARD
SET TITLE = #{title},
	CONTENT = #{content},
	CONTENT_CODE = #{contentCode}
WHERE BOARD_NO = #{boardNo}
AND STATUS = 'Y'
