mapper.select:
SELECT SETTING_NO
FROM CREATE_SETTING
WHERE SETTING_TITLE = #{settingTitle}
AND SETTING_VERSION = #{settingVersion}
AND STATUS IN ('Y','N')

SELECT SETTING_CODE
FROM CREATE_SETTING
WHERE SETTING_TITLE = #{settingTitle}
AND SETTING_VERSION = #{settingVersion}
AND STATUS IN ('Y','N')

SELECT SETTING_INFO
FROM CREATE_SETTING
WHERE SETTING_TITLE = #{settingTitle}
AND SETTING_VERSION = #{settingVersion}
AND STATUS IN ('Y','N')

mapper.update:
UPDATE ERROR_BOARD
SET MODIFIED_CODE = #{modifiedCode},
	MODIFIED_INFO = #{modifiedInfo}
WHERE REF_BNO = #{boardNo}

UPDATE REPLY
SET REPLY_CONTENT = #{replyContent}
WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT USER_ID
FROM MEMBER
WHERE NICKNAME = #{nickName}

mapper.update:
UPDATE ERROR_BOARD
SET SOLVED = 'Y'
WHERE REF_BNO = #{boardNo}
