mapper.select:
SELECT SETTING_NO
FROM CREATE_SETTING
WHERE SETTING_TITLE = #{settingTitle}
AND SETTING_VERSION = #{settingVersion}
AND STATUS IN ('Y','N')

SELECT SETTING_CODE
FROM CREATE_SETTING
WHERE SETTING_TITLE = #{settingTitle}
AND SETTING_VERSION = #{settingVersion}
AND STATUS IN ('Y','N')

SELECT SETTING_INFO
FROM CREATE_SETTING
WHERE SETTING_TITLE = #{settingTitle}
AND SETTING_VERSION = #{settingVersion}
AND STATUS IN ('Y','N')
